CREATE DEFINER=`jieLink`@`%` PROCEDURE `sp_DefaultValueExist`()
BEGIN
 DECLARE licProjectNo_ VARCHAR(64);
 DECLARE licSoftwareSN_ VARCHAR(64);
 SELECT ValueText INTO licProjectNo_ FROM sys_key_value_setting WHERE KeyType='33' AND KeyID='LicProjectNo' LIMIT 1;
 SELECT ValueText into licSoftwareSN_ FROM sys_key_value_setting WHERE KeyType='33' AND KeyID='LicSoftwareSN' LIMIT 1;
 #SELECT licProjectNo_;
 #SELECT licSoftwareSN_;
 IF (licProjectNo_ IS NULL OR LENGTH(TRIM(licProjectNo_))=0) AND (licSoftwareSN_ IS NULL OR LENGTH(TRIM(licSoftwareSN_))=0) THEN
  UPDATE sys_key_value_setting SET ValueText='2' WHERE KeyType='32' AND KeyID='EnableNoPlate';
  UPDATE sys_set_park_trafficrules SET PassBarrierOpen=3,PassBarrierOpenOut=3;
 END IF;
END

CREATE DEFINER=`jieLink`@`%` PROCEDURE `SP_DequeueTask`(v_status tinyint,v_priority tinyint)
BEGIN
  declare v_taskId int default 0;
	select  taskID into v_taskId from sh_taskqueue where `Status`=v_status and priority=v_priority order by TaskID LIMIT 1;		
	update sh_taskqueue set `Status` = 1,ExecutionStartTime = SYSDATE() where taskId= v_taskId;
  select TaskId,TaskType,Content,CallbackUrl,CreatedTime from sh_taskqueue where taskId= v_taskId;
END

CREATE DEFINER=`jieLink`@`%` PROCEDURE `sp_VehicleFlow`(`enterID` varchar(36),`outID` varchar(36))
BEGIN
	
 create temporary table enterTable 
		select hour(EnterTime) as EnterTime,count(1) as EnterCount from box_enter_record where EnterDeviceID=enterID
    and EnterTime>= DATE_FORMAT(now(),'%Y-%m-%d') and  EnterTime< DATE_FORMAT(DATE_ADD(now(),INTERVAL 1 DAY),'%Y-%m-%d') 
		group by  hour(EnterTime);
 
 create temporary table outTable 
		select hour(OutTime) as OutTime,count(1) as OutCount from box_out_record where OutDeviceID=outID
		and OutTime>= DATE_FORMAT(now(),'%Y-%m-%d') and  OutTime< DATE_FORMAT(DATE_ADD(now(),INTERVAL 1 DAY),'%Y-%m-%d') 
		group by  hour(OutTime);

 create temporary table billTable 
		select hour(b.PayTime) as PayTime,sum(b.ActualPaid) as ActualPaid from box_bill b 
	  left join box_enter_record r on b.EnterRecordID = r.EnterRecordID
		where r.EnterDeviceID = enterID 
		and PayTime>= DATE_FORMAT(now(),'%Y-%m-%d') and  PayTime< DATE_FORMAT(DATE_ADD(now(),INTERVAL 1 DAY),'%Y-%m-%d') 
		group by  hour(b.PayTime);

	create temporary table timeTable(
			hourIndex int
	);

	set @myIndex = 0;
  WHILE(@myIndex<24) do
		  REPLACE into timeTable(hourIndex) values (@myIndex);
			set @myIndex = @myIndex +1;
	END WHILE;
  
	select t.hourIndex,IFNULL(ActualPaid,0.00) as ActualPaid,ifnull(EnterCount,0) as EnterCount,ifnull(OutCount,0) as OutCount from timeTable t
    left JOIN enterTable e on t.hourIndex=e.EnterTime 
		left JOIN outTable o on t.hourIndex=o.OutTime
		LEFT JOIN billTable b on t.hourIndex = b.PayTime
	order by t.hourIndex;

	DROP TABLE enterTable;
	DROP TABLE outTable;
	DROP TABLE billTable;
	DROP TABLE timeTable;

END

CREATE DEFINER=`jieLink`@`%` PROCEDURE `add_boxdoordoorrecord`(
  imprt_opertorNO VARCHAR(64), #操作员编号
  imprt_personId VARCHAR(64), #用户编号（人事或访客）
  imprt_credentialNO VARCHAR(128), #凭证编号
  imprt_credentialType VARCHAR(128), #凭证类型
  imprt_deviceId VARCHAR(64),  #设备id（控制器）
  imprt_doorId VARCHAR(64),  #设备id（门或控制器）  
  imprt_controlTypes VARCHAR(256), #门禁控制器类型
  imprt_lyControlTypes VARCHAR(256), #领御控制器类型  
  OUT opertorName_ VARCHAR(36),
  OUT nisspCardId VARCHAR(36),
  OUT cardNum_ VARCHAR(128), 
  OUT outCredentialType VARCHAR(36), #凭证类型，当为领御时 还要作为输出;非领御直接为imprt_credentialType
  OUT personNo_ VARCHAR(64),
  OUT personName_ VARCHAR(128),
  OUT phoneNo_ VARCHAR(32),
  OUT nissPGUID VARCHAR(36),
  OUT deviceName_ VARCHAR(36),
  OUT doorName_ VARCHAR(36),
  OUT visitGuid_ VARCHAR(36), #访客GUID,用来判断是不是访客
  OUT inOutMode_ INT(11)
)
BEGIN
           #定义 变量
      DECLARE indexId int(11) DEFAULT 0;
      DECLARE indexMaxId int(11) DEFAULT 5;
      DECLARE deviceType_ INT(11) DEFAULT 0;
      DECLARE tempCredentialType VARCHAR(36); #凭证类型，当为领御时 要返回给imprt_credentialType
      DECLARE tempDynamicPass VARCHAR(36); #10进制动态密码
      DECLARE controlInOutMode_ INT(11) DEFAULT 0; #非领御控制器
      DECLARE doorInOutMode_ INT(11) DEFAULT 0; #领御控制器
      DECLARE tempCardNum VARCHAR(36); #临时卡面号，兼容集中管控模式时card_info无数据时直接查凭证表
      SET deviceName_="";
      SET doorName_="";
      SET inOutMode_=0;
      #找设备
       SELECT DeviceType,DeviceName,IoType into deviceType_,deviceName_,controlInOutMode_ FROM control_devices WHERE DeviceID=imprt_deviceId LIMIT 1;
       SELECT DeviceName,IoType into doorName_,doorInOutMode_ FROM control_devices WHERE DeviceID=imprt_doorId LIMIT 1;
      if doorInOutMode_ IS NULL or doorInOutMode_=0 THEN
       IF controlInOutMode_ IS NOT NULL AND controlInOutMode_!=0 THEN
        SET inOutMode_ = controlInOutMode_;
       ELSE 
        SET inOutMode_ = 0;
       END IF;
      ELSE       
       SET inOutMode_ = doorInOutMode_;
      END IF;
     #判断是否是动态密码
     IF imprt_credentialType=58 THEN
      set tempDynamicPass=conv(imprt_credentialNO,16,10);
      SET outCredentialType=58;
      SELECT VisitorNo,VisitorName,Telephone,VGUID INTO personNo_,personName_,phoneNo_,visitGuid_ from bd_visitor_info WHERE DynamicPass= tempDynamicPass AND VStatus!=2 AND VStatus!=9 AND `Status`=0 ORDER BY BookingTime DESC LIMIT 1;
      SELECT CloudCardId,VGUID INTO nisspCardId,visitGuid_ FROM bd_visitor_voucher where VGuid=visitGuid_ and `Status`!=4 ORDER BY AddTime DESC limit 1;
      IF visitGuid_ is NULL OR LENGTH(TRIM(visitGuid_))<1 THEN
       SET personNo_="";
       SET personName_="";
       SET phoneNo_="";
      END IF;
     ELSE
      #判断凭证编号是否有值：包括刷卡 app 远程，有的凭证判断完为空要接着判断用户编号
      IF (imprt_credentialNO IS NOT NULL AND LENGTH(TRIM(imprt_credentialNO))>1 and imprt_credentialNO!='00000000') THEN
      #判断凭证编号是否有值：带字母，密码凭证，不是低于1万的纯数字 #正则表达式在sql语句中比较耗时
      #IF(imprt_credentialNO REGEXP '[A-Za-z]' OR imprt_credentialType='161' OR ((imprt_credentialNO REGEXP '[A-Za-z]')=0 AND imprt_credentialNO+0<10000)>=0) THEN
       #有值，先通过凭证编号找    
       SELECT GUID,pguid,CardNum,VoucherType INTO nisspCardId,nissPGUID,tempCardNum,tempCredentialType FROM control_voucher WHERE VoucherNo=imprt_credentialNO AND `Status`!=4 LIMIT 1;       
       SELECT PGUID,PersonNo,PersonName,Mobile INTO nissPGUID,personNo_,personName_,phoneNo_ FROM control_person WHERE STATUS=0 AND pguid=nissPGUID LIMIT 1;
       IF nissPGUID IS NULL THEN        
        #查访客表
        SELECT CloudCardId,CardNum,VGUID INTO nisspCardId,cardNum_,visitGuid_ FROM bd_visitor_voucher WHERE ((VoucherNo IS not NULL AND TRIM(VoucherNo)<>'' AND imprt_credentialNO LIKE CONCAT('%',VoucherNo,'%')) OR imprt_credentialNO LIKE CONCAT('%',VisitorNo,'%')) AND `Status`!=4 ORDER BY AddTime DESC LIMIT 1;
        IF visitGuid_ IS NULL THEN
         #人事 访客均无数据：imprt_personId判断通过操作员找还是通过用户找 
         IF imprt_personId IS NOT null AND LENGTH(TRIM(imprt_personId))>1 THEN
          SELECT PGUID,PersonNo,PersonName,Mobile INTO nissPGUID,personNo_,personName_,phoneNo_ FROM control_person WHERE STATUS=0 AND PersonId=imprt_personId LIMIT 1;
          IF nissPGUID IS NULL THEN
           #查访客表
           SELECT VisitorNo,VisitorName,Telephone,VGUID INTO personNo_,personName_,phoneNo_,visitGuid_ from bd_visitor_info WHERE VisitorNo=imprt_personId AND `Status`!=1 AND VStatus!=2 AND VStatus!=9 ORDER BY BookingTime DESC LIMIT 1;       
           IF visitGuid_ IS NULL THEN
            #人事 访客均无数据，找操作员
            SELECT UserName,Tel into opertorName_,phoneNo_ FROM sys_user WHERE id=imprt_opertorNO AND STATUS=0 LIMIT 1;
            SET personName_ = opertorName_;
           ELSE
            SELECT CloudCardId,CardNum,VGUID INTO nisspCardId,cardNum_,visitGuid_ FROM bd_visitor_voucher where VGuid=visitGuid_ and `Status`!=4 ORDER BY AddTime DESC LIMIT 1;
           END IF;      
          END IF;
         ELSE
          #找操作员
          SELECT UserName,Tel into opertorName_,phoneNo_ FROM sys_user WHERE id=imprt_opertorNO AND STATUS=0 LIMIT 1;
          SET personName_ = opertorName_;     
         END IF;
        ELSE 
         SELECT VisitorNo,VisitorName,Telephone,VGUID INTO personNo_,personName_,phoneNo_,visitGuid_ from bd_visitor_info WHERE VGUID=visitGuid_ AND `Status`!=1 AND VStatus!=2 AND VStatus!=9 ORDER BY BookingTime DESC LIMIT 1;      
        END IF;
       END IF;
       #凭证类型：纸票的话直接赋值，因为有的二维码是虚拟卡或其他类型凭证生成的
       IF imprt_credentialType=56 THEN
        SET outCredentialType = imprt_credentialType;
       ELSE
        IF (tempCredentialType is not NULL AND LENGTH(TRIM(tempCredentialType))>1) THEN
         SET outCredentialType = tempCredentialType;
        ELSE
         SET outCredentialType = imprt_credentialType;
