CREATE DEFINER=`jieLink`@`%` PROCEDURE `add_visitordevice`()
BEGIN
           #定义 变量  
           DECLARE personGuid_ VARCHAR(36) CHARACTER SET utf8 COLLATE utf8_unicode_ci;
					 DECLARE visitorGuid_ VARCHAR(36) CHARACTER SET utf8 COLLATE utf8_unicode_ci;
           DECLARE personDeviceGuid_ longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci;
					 DECLARE configDeviceId_ text CHARACTER SET utf8 COLLATE utf8_unicode_ci;
					 DECLARE configDeviceIdCount_ int(11) default 0;	#访客策略包含的设备数量
					 DECLARE configIndex_ int(11) default 0;	#访客策略循环时下标
					 DECLARE configDeviceGuid_ longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci;
					 DECLARE configDeviceGuidCount_ int(11) default 0;	#访客策略包含的设备数量：查找过设备列表
					 DECLARE configDeviceGuidIndex_ int(11) default 0;	#访客策略循环时下标：查找过设备列表
					 DECLARE personVisitorCount_ int(11) default 0;	#某用户的访客数量
					 DECLARE visitorDeviceCount_ int(11) default 0;	#访客的设备权限数量
					 DECLARE visitorDeviceIndex_ int(11) default 0;	#访客的设备权限数量
					 DECLARE visitorDeviceGuid_ longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci;	#访客的设备权限
           DECLARE tempDeviceId_ VARCHAR(36) CHARACTER SET utf8 COLLATE utf8_unicode_ci;	#设备Id
					 DECLARE tempDeviceGuid_ VARCHAR(36) CHARACTER SET utf8 COLLATE utf8_unicode_ci;	#设备Guid

           #这个用于处理游标到达最后一行的情况  
           DECLARE s int default 0;  
           DECLARE visitorS int default 0;  
           DECLARE tkS int default 0;  
           DECLARE visitorTKS int default 0;  
					 
           #声明游标cursor_person（cursor_person是个多行结果集）
					 DECLARE cursor_person CURSOR FOR select pguid from control_person where `Status`=0 AND PGUID in (select PersonGuid from bd_visitor_info WHERE `Status`=0 AND (VStatus=0 OR VStatus=1));
					 DECLARE cursor_visitor CURSOR FOR select VGUID from bd_visitor_info WHERE `Status`=0 AND (VStatus=0 OR VStatus=1) AND LENGTH(TRIM(IFNULL(PersonGuid,'')))>0 AND VGUID NOT in (SELECT PGUID from control_person_device_relation WHERE ISDeleted=0) ORDER BY BookingTime asc;	-- 排除已经生成了访客与设备关系的访客
					 DECLARE cursor_tkperson CURSOR FOR select pguid from control_person where `Status`=0 AND PGUID in (select PersonGuid from bd_visitor_info WHERE `Status`=0 AND (VStatus=0 OR VStatus=1)) AND PGUID in (SELECT PersonGUID from tk_permission);
					 DECLARE cursor_tkvisitor CURSOR FOR select VGUID from bd_visitor_info WHERE `Status`=0 AND (VStatus=0 OR VStatus=1) AND LENGTH(TRIM(IFNULL(PersonGuid,'')))>0 AND PersonGUID in (SELECT PersonGUID from tk_permission) AND VGUID NOT in (SELECT PersonGUID from tk_permission) ORDER BY BookingTime asc;	-- 排除已经生成了访客与梯控设备关系的访客
           
					 -- 访客的control_person_device_relation
    			 select COUNT(VGUID) into personVisitorCount_ from bd_visitor_info WHERE `Status`=0 AND (VStatus=0 OR VStatus=1) AND LENGTH(TRIM(IFNULL(PersonGuid,'')))>0 AND VGUID NOT in (SELECT PGUID from control_person_device_relation WHERE ISDeleted=0);
					 IF personVisitorCount_ > 0 THEN
						SET personVisitorCount_=0;
						-- 访客策略的设备Id转设备Guid，与用户设备Guid取交集时可直接使用configDeviceGuid_
						SELECT DeviceIdList into configDeviceId_ from sync_visitconfig LIMIT 1;
						IF configDeviceId_ is NOT NULL AND LENGTH(TRIM(configDeviceId_)>0) THEN
						SET configDeviceIdCount_=length(configDeviceId_)-length(REPLACE(configDeviceId_,',',''))+1;
						SET configIndex_=1;
						WHILE configIndex_ <= configDeviceIdCount_ DO
							set tempDeviceId_ = SUBSTRING_INDEX(SUBSTRING_INDEX(configDeviceId_,',',configIndex_),',',-1);
							SELECT DGUID into tempDeviceGuid_ from control_devices where DeviceID=tempDeviceId_ AND DeviceType!=102 LIMIT 1;	-- 排除梯控
							IF tempDeviceGuid_ is NOT NULL AND LENGTH(TRIM(tempDeviceGuid_)>0) THEN
								SET configDeviceGuid_ = CONCAT(IFNULL(configDeviceGuid_,''),',',tempDeviceGuid_);
								SET configDeviceGuidCount_=configDeviceGuidCount_+1;
							END IF;
							SET configIndex_=configIndex_+1;
						END WHILE;
					  SET configDeviceGuid_ = TRIM(BOTH ',' FROM IFNULL(configDeviceGuid_,''));

						IF configDeviceGuid_ is NOT NULL AND LENGTH(TRIM(configDeviceGuid_)>0) AND configDeviceGuidCount_>0 THEN
							BEGIN
							#设置一个终止标记   
							DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET s=1;
							#打开游标  
                OPEN cursor_person;  
                    #获取游标当前指针的记录，读取一行数据并传给变量a,b  
                    fetch cursor_person into personGuid_;  
                    #开始循环，判断是否游标已经到达了最后作为循环条件  
                    while s <> 1 do	
											SELECT DGUID into personDeviceGuid_ from control_person_device WHERE PGUID=personGuid_ LIMIT 1;
											IF personDeviceGuid_ is NOT NULL AND LENGTH(TRIM(personDeviceGuid_)>0) THEN
												select COUNT(VGUID) into personVisitorCount_ from bd_visitor_info WHERE `Status`=0 AND (VStatus=0 OR VStatus=1) AND LENGTH(TRIM(IFNULL(PersonGuid,'')))>0 AND PersonGuid=personGuid_;
												IF personVisitorCount_>0 THEN
													-- 获取访客的设备权限; 
														SET configDeviceGuidIndex_=1;
														WHILE configDeviceGuidIndex_ <= configDeviceGuidCount_ DO
															set tempDeviceGuid_ = SUBSTRING_INDEX(SUBSTRING_INDEX(configDeviceGuid_,',',configDeviceGuidIndex_),',',-1);
															IF FIND_IN_SET(tempDeviceGuid_,personDeviceGuid_)>0 THEN
																SET visitorDeviceGuid_ = CONCAT(IFNULL(visitorDeviceGuid_,''),',',tempDeviceGuid_);
																SET visitorDeviceCount_=visitorDeviceCount_+1;
															END IF;
															SET configDeviceGuidIndex_=configDeviceGuidIndex_+1;
														END WHILE;
														SET visitorDeviceGuid_ = TRIM(BOTH ',' FROM IFNULL(visitorDeviceGuid_,''));
														BEGIN
															DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET visitorS=1;
															#测试存储过程是否执行
															OPEN cursor_visitor;  
																fetch cursor_visitor into visitorGuid_;  
																while visitorS <> 1 do	
																	set visitorDeviceIndex_=1;
																	WHILE visitorDeviceIndex_ <= visitorDeviceCount_ DO
																		set tempDeviceGuid_ = SUBSTRING_INDEX(SUBSTRING_INDEX(visitorDeviceGuid_,',',visitorDeviceIndex_),',',-1);
																		INSERT INTO control_person_device_relation(PDGUID,PGUID,DGUID,ISDeleted,Type) VALUE(UUID(),visitorGuid_,tempDeviceGuid_,0,0);
																		SET visitorDeviceIndex_=visitorDeviceIndex_+1;
														END WHILE;
																	fetch cursor_visitor into visitorGuid_;    
																end while;   
															CLOSE cursor_visitor ;
														END;
												END IF;
											END IF;
                            #读取下一行的数据    
											fetch cursor_person into personGuid_;  
                    end while;  
                 #关闭游标  
                 CLOSE cursor_person ; 
							END;
						END IF;
					END IF;
					 END IF;

					 SET personVisitorCount_=0;
           -- 访客的tk_permission
    			 select COUNT(VGUID) into personVisitorCount_ from bd_visitor_info WHERE `Status`=0 AND (VStatus=0 OR VStatus=1) AND LENGTH(TRIM(IFNULL(PersonGuid,'')))>0 AND PersonGUID in (SELECT PersonGUID from tk_permission) AND VGUID NOT in (SELECT PersonGuid from tk_permission);
					 IF personVisitorCount_ > 0 THEN
						BEGIN
						DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET tkS=1;
						OPEN cursor_tkperson;  
            #获取游标当前指针的记录，读取一行数据并传给变量a,b  
            fetch cursor_tkperson into personGuid_;  
            #开始循环，判断是否游标已经到达了最后作为循环条件  
            while tkS <> 1 do	
							 SET personVisitorCount_=0;
							 select COUNT(VGUID) into personVisitorCount_ from bd_visitor_info WHERE `Status`=0 AND (VStatus=0 OR VStatus=1) AND LENGTH(TRIM(IFNULL(PersonGuid,'')))>0 AND PersonGuid=personGuid_;
							 IF personVisitorCount_ > 0 THEN
								BEGIN
								 DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET visitorTKS=1;
								 #测试存储过程是否执行
									OPEN cursor_tkvisitor;  
									fetch cursor_tkvisitor into visitorGuid_;  
									while visitorTKS <> 1 do	
										INSERT INTO tk_permission(PersonGUID,DeviceId,StartTime,EndTime,IsWhetherPassNumber,PassNumber,IsIgnoreTimeInterval,HouseFloor,InterviewFloor,CreatedName,UpdatedName,CreatedTime,UpdatedTime) SELECT visitorGuid_ AS PersonGUID,DeviceId,StartTime,EndTime,IsWhetherPassNumber,PassNumber,IsIgnoreTimeInterval,HouseFloor,InterviewFloor,CreatedName,UpdatedName,CreatedTime,UpdatedTime FROM tk_permission WHERE PersonGUID=personGuid_;		
										fetch cursor_tkvisitor into visitorGuid_;    
									end while;   
									CLOSE cursor_tkvisitor ;
								END;
							 END IF;
							 fetch cursor_tkperson into personGuid_;  
            end while;  
            #关闭游标  
            CLOSE cursor_tkperson ;
						END;
					 END IF;
    #语句执行结束  
    END

CREATE DEFINER=`jieLink`@`%` PROCEDURE `createvisitorvoucher`()
BEGIN
           #定义 变量  
           DECLARE vguid_v VARCHAR(36);
					 DECLARE visitorNo_v VARCHAR(36);
