           #这个用于处理游标到达最后一行的情况  
           DECLARE s int default 0;  
           #声明游标cursor_visitor
           DECLARE cursor_visitor CURSOR FOR SELECT VGUID,VisitorNo,CredentialNo,CardFaceNo,CredentialType,CredentialPassword,CredentialStatus,Plate,BookingTime FROM bd_visitor_info WHERE `Status`=0 AND VStatus in (0,1) AND CredentialStatus in (1,2) AND Endtime>=CURRENT_DATE;
           #设置一个终止标记     
           DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET s=1;    
						#打开游标  
                OPEN cursor_visitor;
                    #获取游标当前指针的记录，读取一行数据并传给变量a,b
                    fetch cursor_visitor into vguid_v,visitorNo_v,credentialNo_v,cardFaceNo_v,credentialType_v,credentialPassword_v,credentialStatus_v,plate_v,bookingTime_v;  
                    #开始循环，判断是否游标已经到达了最后作为循环条件 
                    while s <> 1 do															
														#根据访客guid、凭证编号、凭证类型、凭证状态判断是否已有该凭证
                            SELECT Guid into guid_temp FROM bd_visitor_voucher WHERE VGuid=vguid_v AND VoucherNo=credentialNo_v AND VoucherType=credentialType_v AND `Status`=credentialStatus_v ORDER BY AddTime DESC LIMIT 1;
														IF guid_temp IS NULL THEN
