															ELSE 
																SET cardFaceNo_v=credentialNo_v;
															END IF;
															#没有，插入
															INSERT INTO bd_visitor_voucher(Guid,VGuid,VisitorNo,VoucherType,VoucherNo,CardNum,PhysicsNum,CardKey,AddOperatorNo,AddTime,UpdateOperatorNo,LastTime,`Status`,Remark)
															VALUE(UUID(),vguid_v,visitorNo_v,credentialType_v,credentialNo_v,cardFaceNo_v,physicsNum_temp,credentialPassword_v,'9999',bookingTime_v,'9999',CURRENT_TIMESTAMP,credentialStatus_v,'访客表凭证数据移植到访客凭证关系表');
														END IF;
														#判断车牌号和凭证号是否一致
														IF plate_v IS NOT NULL && TRIM(LENGTH(plate_v))>0 && plate_v!=credentialNo_v THEN
														#不一致：车牌凭证判断
															SELECT Guid into guid_temp FROM bd_visitor_voucher WHERE VGuid=vguid_v AND VoucherNo=plate_v AND VoucherType=163 AND `Status`=credentialStatus_v ORDER BY AddTime DESC LIMIT 1;
															IF guid_temp IS NULL THEN
																#没有，插入
																INSERT INTO bd_visitor_voucher(Guid,VGuid,VisitorNo,VoucherType,VoucherNo,CardNum,PhysicsNum,CardKey,AddOperatorNo,AddTime,UpdateOperatorNo,LastTime,`Status`,Remark)
																VALUE(UUID(),vguid_v,visitorNo_v,163,plate_v,plate_v,NULL,NULL,'9999',bookingTime_v,'9999',CURRENT_TIMESTAMP,credentialStatus_v,'访客表凭证数据移植到访客凭证关系表');
															END IF;
														END IF;
														#UPDATE bd_visitor_info SET CredentialNo=NULL,CardFaceNo=NULL,CredentialType=NULL,CredentialPassword='',CredentialStatus=0,Plate=NULL WHERE VGuid=vguid_v;
                            -- #读取下一行的数据 
                            fetch cursor_visitor into vguid_v,visitorNo_v,credentialNo_v,cardFaceNo_v,credentialType_v,credentialPassword_v,credentialStatus_v,plate_v,bookingTime_v;													  
                    end while;                     
                 #关闭游标  
                 CLOSE cursor_visitor ;      
    END

